INSERT INTO `product` (`id`, `category_id`, `title`, `alias`, `content`, `price`, `old_price`, `status`, `keywords`, `description`, `img`, `hit`) VALUES
(1, 5, 'Xiaomi Mi8 Pro 8/128Gb Transparent Titanium ', 'xiaomi-mi8-pro', '<p>Стильный флагман Xiaomi Mi8 Pro 8/128Gb Transparent Titanium EU Смартфон Xiaomi Mi8 Pro относится к группе флагманов. Здесь безрамочный дисплей и увеличенная производительность. Презентация этой модели состоялась совсем недавно, но она уже завоевала свою аудиторию. Дизайн и комфорт в эксплуатации Корпус выполнен из прочного стекла, который не подвергается механическим повреждениям.</p>\r\n', 450, 470, '1', '', '<h3>Процессор и память</h3>\r\n<ul><li><strong>Процессор:Qualcomm Snapdragon 845</strong></li>\r\n	<li>Количество ядер процессора:8</li>\r\n	<li>Частота:2.8 ГГц</li>\r\n	<li>Видеопроцессор:Adreno 630</li>\r\n	<li>Встроенная память (ГБ):128</li>\r\n	<li>Оперативная память (ГБ):8\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Спутниковая навигация</h3>\r\n<ul>\r\n	<li>Спутниковая навигация:BeiDou, GPS, ГЛОНАСС\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Связь</h3>\r\n<ul>\r\n	<li>Интернет:3G, 4G, EDGE, GPRS</li>\r\n	<li>Диапазоны GSM:850, 900, 1800, 1900</li>\r\n	<li>Диапазоны UMTS:850, 900, 1900, 2100</li>\r\n	<li>Диапазоны LTE:1, 3, 4, 5, 7, 8, 20, 34, 38, 39, 40, 41</li>\r\n	<li>Bluetooth:5.0</li>\r\n	<li>Wi-Fi (802.11):a, ac, b, g, n</li>\r\n	<li>Разъемы:USB Type-C</li>\r\n	<li>Антенны :LTE антенна B41 4, поддерживает 4 &times; 4 MIMO</li>\r\n	<li>SIM-карта:nano-SIM</li>\r\n	<li>Кол-во SIM-карт:2</li>\r\n	<li>NFC:Полная поддержка NFC\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Дисплей</h3>\r\n<ul>\r\n	<li>Диагональ дисплея:6.21&quot;</li>\r\n	<li>Разрешение дисплея:2248 x 1080 FHD+</li>\r\n	<li>Плотность пикселей (PPI):402\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Фотокамера</h3>\r\n<ul>	<li>Фотокамера (Мп):12 + 12</li>\r\n	<li><strong>Двойная камера:Да</strong></li>	<li>Апертура:f/2.4</li>\r\n	<li><strong>Автофокус</strong></li>\r\n	<li><strong>Фронтальная камера (Мп):20</strong></li>\r\n	<li>Видеозапись</li>\r\n	<li>Разрешение видеосъемки (пикс):1080</li>\r\n	<li>Частота кадров видеосъемки:30 fps</li>\r\n	<li>Beautify-фильтры, Панорамная съёмка, Распознавание лиц\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Питание</h3>\r\n<ul>	<li>Быстрая зарядка:Да</li>\r\n	<li>Тип разъема для зарядки:USB type C</li>\r\n	<li>Емкость аккумулятора (мАч):3000</li>\r\n	<li>Тип:Li-Polimer\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n<h3>Мультимедиа</h3>\r\n\r\n<ul>\r\n	<li>Аудиоразъем:совмещен с USB Type-C</li>\r\n	<li>Поддерживаемые медиа форматы:PCM, AAC/AAC+/eAAC+, MP3, AMR&ndash;NB and WB, FLAC, APE, WAV.\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<h3>Внешний вид</h3>\r\n\r\n<ul>\r\n	<li>Тип корпуса:Классический</li>\r\n	<li>Материал корпуса:Металл, Стекло</li>\r\n	<li>Высота (мм):154,9</li>\r\n	<li>Ширина (мм):74,8</li>\r\n	<li>Толщина (мм):7,6</li>\r\n	<li>Вес (г):177\r\n	<ul>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<h3>Комплектация</h3>\r\n\r\n<ul>\r\n	<li>Mi 8 Pro / Адаптер / USB Type-C кабель / Переходник для наушников с Type-C на 3.5 мм разъем / Инструмент для извлечения SIM-карты / Руководство пользователя / Прозрачный мягкий чехол</li>\r\n</ul>\r\n', 'p-1.png', '1'),
(2, 11, 'Xiaomi Redmi Note 5 3/32 GB Black ', 'xiaomi-redmi-note-5', 'Смартфон Xiaomi Redmi Note 5 3/32GB Black EU — современный гаджет с мощным процессором Xiaomi Redmi Note 5 — доступная и мощная модель, которая придётся по душе каждому. Девайс оснащён высокопроизводительным процессором и благодаря совокупности технических характеристик отлично подойдёт как для игр, так и для решения повседневных задач. Достойные камеры, высокая автономность, отличный экран ', 350, 370, '1', NULL, NULL, 'c-2.jpg', '0'),
(3, 15, 'Meizu 16 th 8/128GB Black', 'meizu-16-th ', 'Смартфон Meizu 16th 8/128GB Black - флагман, идеальный в каждой детали Новая модель компании может похвастаться самым совершенным и высокотехнологичным исполнением. В ней гармонично сочетается все то, что только может присутствовать в современной мобильной технике. Роскошный дизайн, строгий черный корпус, сверхчеткие камеры, прогрессивный процессор и несравненные решения безопасности ', 420, 0, '1', NULL, NULL, 'p-2.png', '1'),
(4, 13, 'Huawei Honor 7C 32GB AUM-L41 Золотистый', 'huawei-honor-7c-32gb-aum-l41 ', '<p>Золотистый Huawei Honor 7C 32GB AUM-L41 - бестселлер среди недорогих гаджетов Huawei Honor 7C 32GB AUM-L41 &ndash; интересная модель смартфона с точки зрения характеристик и цены. Это стандартный, недорогой гаджет, который оборудовали современной и удобной функцией &ndash; NFC. Она предоставляет возможность оплачивать покупки без денег и карты. Еще аппарат оснащен вытянутым экраном, двойной камерой</p>\r\n', 415, 0, '1', '', '<h3>Операционная система</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Операционная система</td>\r\n			<td>Android 8.0</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Дисплей</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Диагональ/разрешение</td>\r\n			<td>5.7&quot;/1440x720 пикс.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Диагональ экрана</td>\r\n			<td>5.7&quot;(14.5 см)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Количество цветов дисплея</td>\r\n			<td>16 млн.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Тип стекла</td>\r\n			<td>2.5D</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Процессор</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Производитель процессора</td>\r\n			<td>Qualcomm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Тип процессора</td>\r\n			<td>Snapdragon 430</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Частота процессора</td>\r\n			<td>1.4 ГГц</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Количество ядер</td>\r\n			<td>8?</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Фотокамера</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Двойная камера</td>\r\n			<td>Да</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Разрешение фотокамеры</td>\r\n			<td>13 МПикс+2МПикс</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Автофокус</td>\r\n			<td>Да</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Встроенная вспышка</td>\r\n			<td>Да</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Качество видеосъемки</td>\r\n			<td>1920x1080 Пикс (FullHD)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Разрешение фронтальной камеры</td>\r\n			<td>8 